#include "test_common.h"
#include "base64.h"


void test_encode_and_decode_binary_jpg_image()
{

    const std::string base64_encoded_image_string = "";

    std::cout << "\n********************************************\nTEST ENCODE AND DECODE JPG IMAGE TO BASE 64\n********************************************\n";
  
    std::cout << "\n--> read image in.jpg ..." << std::endl;
    //read image binary data
    std::ifstream file{"in.jpg",std::ios::binary};
    std::stringstream os;
    os << file.rdbuf();
    std::string imageData = os.str();
    //encode to base 64
    std::cout << "--> encode image to base 64 ..." << std::endl;
    std::string base64Image = base64(imageData);

   //decode fro base64
    std::cout << "--> decode image from base 64..." << std::endl;
    std::string decodedImage = decodeBase64(base64Image);
   
    std::cout << "<-- write back decode image named out.jpg ..." << std::endl;
    //write the newfile
    std::istringstream outbuff{decodedImage};
    std::ofstream("out.jpg",std::ios::binary) << outbuff.rdbuf();

    // we might want to inspect the image data in case everything failed so we save it anyway
    ASSERT_STD_STR(base64_encoded_image_string,base64Image,"[ ** TEST FAILED **]:Wrong Encoded Image data to base 64");
    ASSERT_STD_STR(imageData,decodedImage,"[ ** TEST FAILED **]:Wrong Decoded Image Data");

}

